protected CaseDetailBase getCase(RefRecId _caseRecId, boolean _forUpdate = false)
{
    return CaseDetailBase::findRec(_caseRecId, _forUpdate);
}

public CaseLinkedName getLinkedName(RefRecId _parentRecId)
{
    var name = next getLinkedName(_parentRecId);

    if(!strLen(name))
        name = this.getCase(_parentRecId).(this.getFieldToDisplay());

    return name;
}

private FieldId getFieldToDisplay()
{
    return fieldNum(CaseDetailBase, Description);
}
